import { createSlice } from "@reduxjs/toolkit"

export const calculatorActionTypes = {
    ADD:'ADD',
    SUBTRACT:"SUBTRACT",
    DIVIDE:"DIVIDE",
    MULTIPLY:"MULTIPLY"
}

const initialState = {
    result: 0
}


export const calculatorSlice = createSlice({
    reducers:{
        add(state, action){
             state.result += action.payload
        },
        subtract(state, action){
            state.result -= action.payload
       },
       multiply(state, action){
        state.result *= action.payload
       },
       divide(state, action){
        state.result /= action.payload
      }
    }
})

