import { createSlice } from "@reduxjs/toolkit"

export const calculatorActionTypes = {
    ADD:'ADD',
    SUBTRACT:"SUBTRACT",
    DIVIDE:"DIVIDE",
    MULTIPLY:"MULTIPLY"
}

const initialState = {
    result: 0
