import { createSlice } from "@reduxjs/toolkit"


const initialState = {
    email:"",
    password:"",
    isAuthorized: false,
}


export const authSlice = createSlice({
    name:"auth",
    initialState,
    reducers:{
        login(state, action){
          state.email = action.email;
          state.password = action.password;
          state.isAuthorized = true
        },
        logout(){
          return initialState;
        }
    }
})


export const authActions = authSlice.actions;
